settings.json:
{
  "settingsSync.keybindingsPerPlatform": false,
  "editor.defaultFormatter": "esbenp.prettier-vscode",
  "vim.easymotion": true,
  "vim.incsearch": true,
  "vim.useSystemClipboard": true,
  "vim.useCtrlKeys": false,
  "vim.hlsearch": true,
  "vim.insertModeKeyBindings": [
    {
      "before": ["j", "k"],
      "after": ["<Esc>"]
    }
  ],
  "vim.normalModeKeyBindingsNonRecursive": [
    {
      "before": ["<C-n>"],
      "commands": [":nohl"]
    },
    {
      "before": ["J"],
      "after": ["5", "j"]
    },
    {
      "before": ["K"],
      "after": ["5", "k"]
    },
    {
      "before": ["H"],
      "after": ["5", "h"]
    },
    {
      "before": ["L"],
      "after": ["5", "l"]
    },
    {
      "before": ["<leader>", "a"],
      "after": ["0"]
    },
    {
      "before": ["<leader>", "e"],
      "after": ["$"]
    },
    {
      "before": ["<leader>", "f"],
      "after": ["<leader>", "<leader>", "f"]
    },
    // debug keymap shortcut
    {
      "before": ["<leader>", "d", "d"],
      "commands": ["editor.debug.action.toggleBreakpoint"]
    },
    {
      "before": ["<leader>", "d", "r"],
      "commands": ["workbench.action.debug.start"]
    },
    {
      "before": ["<leader>", "d", "n"],
      "commands": ["workbench.action.debug.stepInto"]
    },
    {
      "before": ["<leader>", "d", "p"],
      "commands": ["workbench.action.debug.stepOut"]
    },
    {
      "before": ["<leader>", "d", "c"],
      "commands": ["workbench.action.debug.continue"]
    },
    {
      "before": ["<leader>", "d", "s"],
      "commands": ["workbench.action.debug.stop"]
    }
  ],
  "vim.leader": "<space>",
  "vim.handleKeys": {
    "<C-a>": false,
    "<C-f>": false
  },
  "files.autoSave": "onFocusChange",
  "autoprefixer.formatOnSave": true,
  "editor.fontFamily": "'JetbrainsMono NF', 'Fira Code', Consolas, 'Courier New', monospace",
  "editor.fontLigatures": true,
  "files.exclude": {
    "**/.git": true,
    "**/.svn": true,
    "**/.hg": true,
    "**/CVS": true,
    "**/.DS_Store": true,
    "**/Thumbs.db": true
    // "**/node_modules": true
  },
  "workbench.iconTheme": "material-icon-theme",
  "explorer.confirmDelete": false,
  "security.workspace.trust.untrustedFiles": "open",
  "prettier.jsxSingleQuote": true,
  "editor.fontSize": 16,
  "editor.unicodeHighlight.nonBasicASCII": false,
  "typescript.updateImportsOnFileMove.enabled": "always",
  "vim.easymotionDimColor": "#3197ff",
  "[markdown]": {
    "editor.wordWrap": "on",
    "editor.quickSuggestions": {
      "other": true,
      "comments": false,
      "strings": false
    },
    "editor.formatOnSave": true
  },
  // "prettier.semi": false,
  "leetcode.endpoint": "leetcode-cn",
  "material-icon-theme.folders.theme": "specific",
  "window.menuBarVisibility": "toggle",
  "javascript.updateImportsOnFileMove.enabled": "always",
  "better-comments.tags": [
    {
      "tag": "!",
      "color": "#FF2D00",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "?",
      "color": "#3498DB",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "//",
      "color": "#474747",
      "strikethrough": true,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "todo",
      "color": "#FF8C00",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "*",
      "color": "#98C379",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    }
  ],
  "liveServer.settings.port": 3290,
  "workbench.activityBar.visible": false,
  "workbench.editor.splitInGroupLayout": "vertical",
  "workbench.startupEditor": "none",
  "git.confirmSync": false,
  "editor.lineNumbers": "relative",
  "workbench.colorTheme": "Ayu Dark Bordered"
}
